class Modal{get Node(){return this._obj}get Classes(){return this._classes}get OnAccepts(){return this._onaccepts}get Dialog(){return this._obj.getElementsByClassName(this._classes.dialog)[0]}constructor(t,e={close:"modal-close",accept:"modal-accept",container:"modal-container",dialog:"modal-dialog"}){this._obj=t,this._classes=e,this._onaccepts=[],this.init()}init(){let t=this;this._obj.addEventListener("click",e=>{e.classList.contains(t._classes.container)&&t.close()});let e=this._obj.getElementsByClassName(this._classes.close);for(let s=0;s<e.length;s++){e[s].addEventListener("click",()=>{t.close()})}let s=this._obj.getElementsByClassName(this._classes.close);for(let e=0;e<s.length;e++){const c=s[e];c.addEventListener("click",()=>{t.accept(c)})}}open(){this._obj.setAttribute("hidden","")}close(){this._obj.removeAttribute("hidden")}accept(t){for(let e=0;e<this._onaccepts.length;e++)this._onaccepts[e](this,t)}addActivator(t){let e=this;t.addEventListener("click",()=>{e.open()})}addActivators(t){let e=this;for(let s=0;s<t.length;s++){t[s].addEventListener("click",()=>{e.open()})}}addClose(t){let e=this;t.addEventListener("click",()=>{e.close()})}addAcceptor(t){let e=this;t.addEventListener("click",()=>{e.accept(t)})}onAccept(t){this._onaccepts.push(t)}}